mapper.select:

select 

from mp_agent_charge_record where del_flag = 1 

   and agent_id = #{agentId,jdbcType=VARCHAR}


   and order_id = #{orderId,jdbcType=DECIMAL}


   and phone = #{phone,jdbcType=VARCHAR}


   and biz_type = #{bizType,jdbcType=CHAR}


   and fee = #{fee,jdbcType=DECIMAL}


   and value = #{value,jdbcType=DECIMAL}


   and discount = #{discount,jdbcType=DECIMAL}


   and apply_date = #{applyDate,jdbcType=TIMESTAMP}


   and end_date = #{endDate,jdbcType=TIMESTAMP}

order by apply_date desc

mapper.insert:

insert into mp_agent_charge_record (id, agent_id, order_id, 
  phone, biz_type, fee, value, 
  discount, apply_date, end_date
  )
values (#{id,jdbcType=VARCHAR}, #{agentId,jdbcType=VARCHAR}, #{orderId,jdbcType=DECIMAL}, 
  #{phone,jdbcType=VARCHAR}, #{bizType,jdbcType=CHAR}, #{fee,jdbcType=DECIMAL}, #{value,jdbcType=DECIMAL}, 
  #{discount,jdbcType=DECIMAL}, #{applyDate,jdbcType=TIMESTAMP}, #{endDate,jdbcType=TIMESTAMP}
  )
