mapper.select:

select 

from mp_bill_discount_model where del_flag = 1 

    and name like concat('%',#{name,jdbcType=VARCHAR},'%') 


   and update_user = #{updateUser,jdbcType=VARCHAR}


   and update_date = #{updateDate,jdbcType=TIMESTAMP}


   and create_user = #{createUser,jdbcType=VARCHAR}


   and create_date = #{createDate,jdbcType=TIMESTAMP}


select 
count(1)
from mp_bill_discount_model where del_flag = 1 

   and name = #{name,jdbcType=VARCHAR}


   and update_user = #{updateUser,jdbcType=VARCHAR}


   and update_date = #{updateDate,jdbcType=TIMESTAMP}


   and create_user = #{createUser,jdbcType=VARCHAR}


   and create_date = #{createDate,jdbcType=TIMESTAMP}

mapper.insert:
select  replace(uuid(),'-','') as myuuid  from dual
  
  insert into mp_bill_discount_model (id, name, create_user, create_date
    )
  values (#{myuuid,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{createUser,jdbcType=VARCHAR}, CURRENT_TIMESTAMP
    )
