select a.order_id as orderId,a.agent_order_id as agentOrderId, a.provider_id as providerId,a.bill_type as billType, a.phone,a.fee,`value`,b.province_code as provinceCode, a.pkg_id as pkgId,a.agent_discount as agentDiscount, b.city_code as cityCode,c.`name` as providerName,d.id as agentId, d.`name` as agentName,e.`name` as groupName, case when count(f.id) > 0 then '是' else '否' end yesOrNo from mp_exception_order a left join mp_phone_section b on substr(a.phone,1,7) = b.section left join mp_provider c on b.provider_id = c.id left join mp_agent d on a.agent_id = d.id left join mp_provider_bill_group e on d.bill_group_id = e.id left join mp_provider_group_bill_rel f on a.provider_id = f.provider_id and d.bill_group_id = f.group_id and (b.province_code = f.province_code or f.province_code = '全国') where 1=1
and a.agent_order_id = #{agentOrderId,jdbcType=VARCHAR}
and a.phone = #{phone,jdbcType=VARCHAR}

select * from mp_exception_order where 1=1

	and agent_order_id = #{agentOrderId,jdbcType=VARCHAR}


	and phone = #{phone,jdbcType=VARCHAR}

order by apply_date desc limit 1
