mapper.insert:
select  replace(uuid(),'-','') as myuuid  from dual
insert into mp_provider_bill_group (id, name, create_date, create_user )
values (#{myuuid,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, 
CURRENT_TIMESTAMP, #{create_user,jdbcType=VARCHAR}
  )

mapper.update:
update mp_provider_bill_group
     
    name = #{name,jdbcType=VARCHAR},            
    update_date = CURRENT_TIMESTAMP , 
    update_user = #{update_user,jdbcType=VARCHAR}

where id = #{id,jdbcType=VARCHAR}
