mapper.select:
SELECT DISTINCT brand FROM channel_object WHERE 1 = 1
AND type = #{type,jdbcType=VARCHAR}
ORDER BY brand

SELECT
	IFNULL(SUM(
		a.fee*a.agent_discount
	),0)
FROM
	mp_order a 
LEFT JOIN report_object b ON b.object_id = a.agent_id
WHERE a.status = 3

	AND a.submittime >= #{startDate,jdbcType=VARCHAR}


	AND a.submittime < #{endDate,jdbcType=VARCHAR}

SELECT IFNULL(SUM(fee*provider_discount),0) FROM mp_order WHERE status = 3
AND submittime >= #{startDate,jdbcType=VARCHAR}
AND submittime < #{endDate,jdbcType=VARCHAR}

SELECT COUNT(1) AS count, IFNULL(SUM(a.fee),0) AS fee, IFNULL(SUM(a.fee*a.provider_discount),0) AS providerDisprice, IFNULL(SUM( a.fee * a.agent_discount ),0) AS agentDisprice FROM ( SELECT * FROM mp_order WHERE 1 = 1
AND submittime >= #{startDate,jdbcType=VARCHAR}
AND submittime < #{endDate,jdbcType=VARCHAR}
) a LEFT JOIN report_object b ON b.object_id = a.agent_id WHERE a.channelname IN ( SELECT b. NAME FROM channel_object b WHERE 1 = 1
AND b.type = #{type,jdbcType=VARCHAR}
AND b.brand = #{brand,jdbcType=VARCHAR}
) AND STATUS = 3

SELECT b.`name` AS channelName, COUNT(1) AS count, SUM(a.fee) AS fee, SUM(a.fee * a.provider_discount) AS providerFee, SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) AS profit, CONCAT(ROUND(COUNT(1) / c.count * 100, 3),'%') AS countProportion, CONCAT(ROUND(SUM(a.fee) / c.fee * 100, 3),'%') AS feeProportion, CONCAT(ROUND( SUM(a.fee * a.provider_discount) / c.providerFee * 100, 3 ),'%') AS providerProportion, CONCAT(ROUND( SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) / c.profit * 100, 3 ),'%') AS profitProportion FROM mp_order a LEFT JOIN mp_provider_physical_channel b ON a.dispatcher_provider_id = b.id, ( SELECT COUNT(1) AS count, SUM(fee) AS fee, SUM(fee * provider_discount) AS providerFee, SUM( fee * ( agent_discount - provider_discount ) ) AS profit FROM mp_order WHERE `status` = 3
AND apply_date >= #{startDate,jdbcType=VARCHAR}
AND apply_date < #{endDate,jdbcType=VARCHAR}
) c WHERE `status` = 3
AND apply_date >= #{startDate,jdbcType=VARCHAR}
AND apply_date < #{endDate,jdbcType=VARCHAR}
GROUP BY a.dispatcher_provider_id

SELECT COUNT(1) AS count, SUM(a.fee) AS fee, a.agent_id AS agentId, b.nickname, SUM(a.fee * a.agent_discount) AS agentFee, SUM(a.fee * a.provider_discount) AS providerFee, SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) AS profit, CONCAT( ROUND(COUNT(1) / c.count * 100, 3), '%' ) AS countProportion, CONCAT( ROUND(SUM(a.fee) / c.fee * 100, 3), '%' ) AS feeProportion, CONCAT( ROUND( SUM(a.fee * a.provider_discount) / c.providerFee * 100, 3 ), '%' ) AS providerProportion, CONCAT( ROUND( SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) / c.profit * 100, 3 ), '%' ) AS profitProportion FROM mp_order a LEFT JOIN mp_agent b ON a.agent_id = b.id, ( SELECT COUNT(1) AS count, SUM(fee) AS fee, SUM(fee * provider_discount) AS providerFee, SUM( fee * ( agent_discount - provider_discount ) ) AS profit FROM mp_order WHERE `status` = 3
AND apply_date >= #{startDate,jdbcType=VARCHAR}
AND apply_date < #{endDate,jdbcType=VARCHAR}
) c WHERE a.`status` = 3
AND a.apply_date >= #{startDate,jdbcType=VARCHAR}
AND a.apply_date < #{endDate,jdbcType=VARCHAR}
GROUP BY agent_id

SELECT DATE_FORMAT(a.apply_date, '%Y-%m-%d') AS date, b.`name` AS channelName, COUNT(1) AS count, SUM(a.fee) AS fee, SUM(a.fee * a.agent_discount) AS agentFee, SUM(a.fee * a.provider_discount) AS providerFee, SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) AS profit FROM ( SELECT apply_date,fee,agent_discount,provider_discount,dispatcher_provider_id FROM mp_order WHERE `status` = 3
AND apply_date > #{startDate,jdbcType=VARCHAR}
AND apply_date <= #{endDate,jdbcType=VARCHAR}
) a LEFT JOIN mp_provider_physical_channel b ON a.dispatcher_provider_id = b.id WHERE 1 = 1
AND b.`name` LIKE concat(concat('%',#{channelName,jdbcType = VARCHAR}),'%')
GROUP BY a.dispatcher_provider_id, DATE_FORMAT(a.apply_date, '%Y%m%d')

SELECT count(1) FROM (SELECT DATE_FORMAT(a.apply_date, '%Y-%m-%d') AS date, b.`name` AS channelName, COUNT(1) AS count, SUM(a.fee) AS fee, SUM(a.fee * a.agent_discount) AS agentFee, SUM(a.fee * a.provider_discount) AS providerFee, SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) AS profit FROM ( SELECT apply_date,fee,agent_discount,provider_discount,dispatcher_provider_id FROM mp_order WHERE `status` = 3
AND apply_date > #{startDate,jdbcType=VARCHAR}
AND apply_date <= #{endDate,jdbcType=VARCHAR}
) a LEFT JOIN mp_provider_physical_channel b ON a.dispatcher_provider_id = b.id WHERE 1 = 1
AND b.`name` LIKE concat(concat('%',#{channelName,jdbcType = VARCHAR}),'%')
GROUP BY a.dispatcher_provider_id, DATE_FORMAT(a.apply_date, '%Y%m%d')) p

SELECT DATE_FORMAT(a.apply_date, '%Y-%m-%d') AS date, b.`name` AS agentName, COUNT(1) AS count, SUM(a.fee) AS fee, SUM(a.fee * a.agent_discount) AS agentFee, SUM(a.fee * a.provider_discount) AS providerFee, SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) AS profit FROM ( SELECT apply_date, fee, agent_discount, provider_discount, agent_id FROM mp_order WHERE `status` = 3
AND apply_date > #{startDate,jdbcType=VARCHAR}
AND apply_date <= #{endDate,jdbcType=VARCHAR}
) a LEFT JOIN mp_agent b ON a.agent_id = b.id WHERE 1 = 1
AND b.`name` LIKE concat(concat('%',#{agentName,jdbcType = VARCHAR}),'%')
GROUP BY a.agent_id, DATE_FORMAT(a.apply_date, '%Y%m%d')

SELECT count(1) FROM (SELECT DATE_FORMAT(a.apply_date, '%Y-%m-%d') AS date, b.`name` AS agentName, COUNT(1) AS count, SUM(a.fee) AS fee, SUM(a.fee * a.agent_discount) AS agentFee, SUM(a.fee * a.provider_discount) AS providerFee, SUM( a.fee * ( a.agent_discount - a.provider_discount ) ) AS profit FROM ( SELECT apply_date, fee, agent_discount, provider_discount, agent_id FROM mp_order WHERE `status` = 3
AND apply_date > #{startDate,jdbcType=VARCHAR}
AND apply_date <= #{endDate,jdbcType=VARCHAR}
) a LEFT JOIN mp_agent b ON a.agent_id = b.id WHERE 1 = 1
AND b.`name` LIKE concat(concat('%',#{agentName,jdbcType = VARCHAR}),'%')
GROUP BY a.agent_id, DATE_FORMAT(a.apply_date, '%Y%m%d')) p

SELECT c.suName as channelPerson, COUNT(1) as count, SUM(fee) as fee, SUM(fee * agent_discount) as agentFee, SUM(fee * provider_discount) as providerFee, SUM(fee * (agent_discount - provider_discount)) as profit FROM mp_order a LEFT JOIN mp_agent b ON a.agent_id = b.id LEFT JOIN sysusert c on b.channel_person = suId WHERE a.`status` = 3
AND apply_date > #{startDate,jdbcType=VARCHAR}
AND apply_date <= #{endDate,jdbcType=VARCHAR}
GROUP BY b.channel_person
