mapper.select:

select 

from 
	mp_phone_section a
left join
 	mp_provider b
on a.provider_id = b.id
where 
	a.del_flag=1

   and a.section like concat('%',#{section,jdbcType=VARCHAR},'%') 


   and a.provider_id = #{providerId,jdbcType=VARCHAR}


   and a.province_code = #{provinceCode,jdbcType=VARCHAR}


   and a.city_code = #{cityCode,jdbcType=VARCHAR}


   and a.update_date = #{updateDate,jdbcType=TIMESTAMP}


   and a.update_user = #{updateUser,jdbcType=VARCHAR}


   and a.create_date = #{createDate,jdbcType=TIMESTAMP}


   and a.create_user = #{createUser,jdbcType=VARCHAR}


   and a.provider_type = #{providerType,jdbcType=VARCHAR}


   and a.carrier_type = #{carrierType,jdbcType=VARCHAR}


   and b.name like concat('%',#{providerName,jdbcType=VARCHAR},'%') 

order by a.create_date desc


select 
	count(1)
from 
	mp_phone_section a
left join
 	mp_provider b
on a.provider_id = b.id
where 
	a.del_flag=1

   and a.section like concat('%',#{section,jdbcType=VARCHAR},'%') 


   and a.provider_id = #{providerId,jdbcType=VARCHAR}


   and a.province_code = #{provinceCode,jdbcType=VARCHAR}


   and a.city_code = #{cityCode,jdbcType=VARCHAR}


   and a.update_date = #{updateDate,jdbcType=TIMESTAMP}


   and a.update_user = #{updateUser,jdbcType=VARCHAR}


   and a.create_date = #{createDate,jdbcType=TIMESTAMP}


   and a.create_user = #{createUser,jdbcType=VARCHAR}


   and a.provider_type = #{providerType,jdbcType=VARCHAR}


   and a.carrier_type = #{carrierType,jdbcType=VARCHAR}


   and b.name like concat('%',#{providerName,jdbcType=VARCHAR},'%')

mapper.insert:
select  replace(uuid(),'-','') as id  from dual
  
  insert into mp_phone_section (id, section, provider_id, 
    province_code, city_code, update_date, 
    update_user, create_date, create_user, provider_type, carrier_type
    )
  values (#{id,jdbcType=VARCHAR}, #{section,jdbcType=VARCHAR}, #{providerId,jdbcType=VARCHAR}, 
    #{provinceCode,jdbcType=VARCHAR}, #{cityCode,jdbcType=VARCHAR}, 
    #{updateDate,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}, CURRENT_TIMESTAMP, 
    #{createUser,jdbcType=VARCHAR},#{providerType,jdbcType=VARCHAR},#{carrierType,jdbcType=VARCHAR}
    )


insert into mp_phone_section (id, section, provider_id, 
  province_code, city_code, update_date, 
  update_user, create_date, create_user, provider_type, carrier_type
  )
values

  (#{record.id,jdbcType=VARCHAR}, #{record.section,jdbcType=VARCHAR}, #{record.providerId,jdbcType=VARCHAR}, 
  #{record.provinceCode,jdbcType=VARCHAR}, #{record.cityCode,jdbcType=VARCHAR}, 
  #{record.updateDate,jdbcType=TIMESTAMP}, #{record.updateUser,jdbcType=VARCHAR}, CURRENT_TIMESTAMP, 
  #{record.createUser,jdbcType=VARCHAR},#{record.providerType,jdbcType=VARCHAR},#{record.carrierType,jdbcType=VARCHAR}
  )

mapper.select:
select 
*
from mp_phone_section where del_flag=1 and section = #{section,jdbcType=VARCHAR}
