mapper.select:

select 

from mp_submit_order where del_flag=1

   and agent_name = #{agentName,jdbcType=VARCHAR}


   and order_id = #{orderId,jdbcType=VARCHAR}


   and submit_param = #{submitParam,jdbcType=VARCHAR}


   and result_code = #{resultCode,jdbcType=VARCHAR}


   and phone = #{phone,jdbcType=VARCHAR}


   and biz_type = #{bizType,jdbcType=CHAR}


   and submit_date = #{submitDate,jdbcType=VARCHAR}


   and submit_date > #{startDate,jdbcType=VARCHAR}


   and submit_date < #{endDate,jdbcType=VARCHAR}

order by submit_date desc


select 
	count(1)
from mp_submit_order where del_flag=1

   and agent_name = #{agentName,jdbcType=VARCHAR}


   and order_id = #{orderId,jdbcType=VARCHAR}


   and submit_param = #{submitParam,jdbcType=VARCHAR}


   and result_code = #{resultCode,jdbcType=VARCHAR}


   and phone = #{phone,jdbcType=VARCHAR}


   and biz_type = #{bizType,jdbcType=CHAR}


   and submit_date = #{submitDate,jdbcType=VARCHAR}


   and submit_date > #{startDate,jdbcType=VARCHAR}


   and submit_date < #{endDate,jdbcType=VARCHAR}
