mapper.select:
  select count(1) from mp_agent_account_charge_audit a LEFT JOIN mp_agent b on a.agent_id=b.id 
	LEFT JOIN  sysusert c on b.channel_person =c.suId where a.del_flag = 1
 	
       and a.id = #{id,jdbcType=VARCHAR}
    
	
       and a.agent_id = #{agentId,jdbcType=VARCHAR}
    
    
       and a.fee = #{fee,jdbcType=DECIMAL}
    
    
       and a.money_account = #{moneyAccount,jdbcType=VARCHAR}
    
    
       and a.type = #{applyUser,jdbcType=VARCHAR}
    
    
       and a.apply_user = #{applyUser,jdbcType=DECIMAL}
    
    
       and a.apply_date >= #{applyDate,jdbcType=TIMESTAMP}
    
    
       and a.confirm_user = #{confirmUser,jdbcType=DECIMAL}
    
    
       and a.confirm_date <= #{confirmDate,jdbcType=TIMESTAMP} 
    
    
       and a.flag = #{flag,jdbcType=INTEGER}
    
    
       and b.name = #{agentName,jdbcType=VARCHAR}
    
    
       and c.suName = #{channelPerson,jdbcType=VARCHAR}

select a.id as id, a.agent_id as agent_id, a.fee as fee, a.money_account as money_account, CASE WHEN a.type = 1 THEN '加款' WHEN a.type = 2 THEN '调账' END AS type, a.apply_user as apply_user, a.apply_date as apply_date, a.confirm_user as confirm_user, a.confirm_date as confirm_date, a.paymoney_name as paymoney_name, a.yue as yue,a.remark as remark, CASE WHEN a.flag = 0 THEN '审核' WHEN a.flag = 1 THEN '审核通过' WHEN a.flag = 2 THEN '审核拒绝' END AS flag, CASE WHEN a.flag = 1 THEN '已审核' WHEN a.flag = 2 THEN '已审核' WHEN a.flag = 0 THEN '0' END AS state, date_format(a.apply_date, '%Y-%m-%d %H:%i:%s') as applyDate, date_format(a.confirm_date, '%Y-%m-%d %H:%i:%s') as confirmDate, b.name as agentName,c.suName as qudao from mp_agent_account_charge_audit a LEFT JOIN mp_agent b on a.agent_id=b.id LEFT JOIN sysusert c on b.channel_person =c.suId where a.del_flag = 1
and a.id = #{id,jdbcType=VARCHAR}
and a.agent_id = #{agentId,jdbcType=VARCHAR}
and a.fee = #{fee,jdbcType=DECIMAL}
and a.money_account = #{moneyAccount,jdbcType=VARCHAR}
and a.type = #{applyUser,jdbcType=VARCHAR}
and a.apply_user = #{applyUser,jdbcType=DECIMAL}
and a.apply_date >= #{applyDate,jdbcType=TIMESTAMP}
and a.confirm_user = #{confirmUser,jdbcType=DECIMAL}
and a.confirm_date <= #{confirmDate,jdbcType=TIMESTAMP}
and a.flag = #{flag,jdbcType=INTEGER}
and b.name = #{agentName,jdbcType=VARCHAR}
and c.suName = #{channelPerson,jdbcType=VARCHAR}
order by a.apply_date desc

select sum(fee) from mp_agent_account_charge_audit a 
	where a.del_flag = 1
	
       and a.id = #{id,jdbcType=VARCHAR}
    
	
       and a.agent_id = #{agentId,jdbcType=VARCHAR}
    
    
       and a.fee = #{fee,jdbcType=DECIMAL}
    
    
       and a.money_account = #{moneyAccount,jdbcType=VARCHAR}
    
     
       and a.type = #{applyUser,jdbcType=VARCHAR}
    
    
       and a.apply_user = #{applyUser,jdbcType=DECIMAL}
    
    
       and a.apply_date >= #{applyDate,jdbcType=TIMESTAMP}
    
    
       and a.confirm_user = #{confirmUser,jdbcType=DECIMAL}
    
    
       and a.confirm_date <= #{confirmDate,jdbcType=TIMESTAMP} 
    
    
       and a.flag = #{flag,jdbcType=INTEGER}
