mapper.select:

select 

from mp_agent_account where del_flag = 1 

   and agent_id = #{agentId,jdbcType=VARCHAR}


   and balance = #{balance,jdbcType=DECIMAL}


   and credit = #{credit,jdbcType=DECIMAL}


   and update_date = #{updateDate,jdbcType=TIMESTAMP}


   and update_user = #{updateUser,jdbcType=VARCHAR}

order by update_date desc

mapper.insert:
select  replace(uuid(),'-','') as id  from dual
  
  insert into mp_agent_account (id, agent_id, balance, 
    credit, update_date, update_user
    )
  values (#{id,jdbcType=VARCHAR}, #{agentId,jdbcType=VARCHAR}, #{balance,jdbcType=DECIMAL}, 
    #{credit,jdbcType=DECIMAL}, #{updateDate,jdbcType=TIMESTAMP}, #{updateUser,jdbcType=VARCHAR}
    )
