mapper.select:

select 

from mp_19e_product where 1 = 1 

   and prod_isptype = #{prodIsptype,jdbcType=VARCHAR}


   and prod_delaytimes = #{prodDelaytimes,jdbcType=VARCHAR}


   and prod_content = #{prodContent,jdbcType=VARCHAR}


   and prod_provinceid = #{prodProvinceid,jdbcType=VARCHAR}


   and prod_price = #{prodPrice,jdbcType=DOUBLE}


   and prod_type = #{prodType,jdbcType=VARCHAR}
