mapper.select:
  	select 
  		count(*)
  	from mp_provider_bill_pkg a
  	LEFT JOIN mp_bill_pkg b ON a.bill_pkg_id=b.id
	LEFT JOIN mp_provider c ON c.id=a.provider_id
	where a.del_flag=1
	 
       and a.provider_id = #{providerId,jdbcType=VARCHAR}
    
	 
       and a.province_code = #{provinceCode,jdbcType=VARCHAR}
    
    
       and a.city_code = #{cityCode,jdbcType=VARCHAR}

    
    select 
    
    from mp_provider_bill_pkg a
  	LEFT JOIN mp_bill_pkg b ON a.bill_pkg_id=b.id
	LEFT JOIN mp_provider c ON c.id=a.provider_id where a.del_flag=1 and a.provider_id=#{providerId,jdbcType=VARCHAR}
    
       and bill_pkg_id = #{billPkgId,jdbcType=VARCHAR}
    
    
       and a.province_code = #{provinceCode,jdbcType=VARCHAR}
    
    
       and a.city_code = #{cityCode,jdbcType=VARCHAR}
    
    
       and update_user = #{updateUser,jdbcType=VARCHAR}
    
    
       and update_date = #{updateDate,jdbcType=TIMESTAMP}
    
    
       and create_user = #{createUser,jdbcType=VARCHAR}
    
    
       and create_date = #{createDate,jdbcType=TIMESTAMP}
    
    ORDER BY  a.province_code,a.city_code,b.price

select 
	b.*,a.province_code,a.city_code
from 
	mp_provider_bill_pkg a 
	left join mp_bill_pkg b on a.bill_pkg_id = b.id
where 
	a.del_flag = 1
	
		and a.provider_id = #{providerId,jdbcType=VARCHAR}
	
	
		and a.province_code = #{provinceCode,jdbcType=VARCHAR} 
	
	order by b.value,b.price;
